package ejerciciopseudocodigorepetitivo1;

public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int suma = 0, i = 1;

		while (i <= 20) {

			suma = suma + i;

			i++;

		}

		System.out.println("La suma de los numeros desde el 1 al 20 es " + suma);
	}

}
