package ejerciciosJAVA1;

public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0, j=1, k=1;
		
		while (i<5) {
			
			while (j<=k) {
				
				System.out.print("*");
				
				j++;
				
			}
			
			System.out.println();
			
			k++;
			
			j=1;
			
			i++;
			
		}

	}

}
